const amqplib = require('amqplib/callback_api');
import { sendMailToNewUser } from './user.util';
const queue = 'register';


export const producer = (data) => amqplib.connect('amqp://localhost', (error, connection) => {
    if (error) throw error;
    connection.createChannel((error, channel1) => {
        if (error) throw error;
        channel1.assertQueue(queue);
        channel1.sendToQueue(queue, Buffer.from(JSON.stringify(data)));
    })
})

export const consumer = () => amqplib.connect('amqp://localhost', (error, connection) => {
    if (error) throw error;
    connection.createChannel((error, channel2) => {
        if (error) throw error;
        channel2.assertQueue(queue);
        channel2.consume(queue, (message) => {
            if (message !== null) {
                const data = JSON.parse(message.content.toString())
                channel2.ack(message);
                console.log("Message Recieved ", data)
                sendMailToNewUser(data.email)
            } else {
                console.log('Consumer did not registered');
            }
        });
    });
});
consumer();
